import React from 'react';
import { useHttp } from '../hooks/useHttp'
import { useLocation } from 'react-router-dom';

function useQuery() {
    return new URLSearchParams(useLocation().search);
}

const Resultados = () => {

    let query = useQuery();
    var busqueda = query.get("search")
    var [resultado] = useHttp('http://localhost:3050/api/items/' + busqueda);
    var items = resultado.items;

    return (

        <>
            <div className="contenedor">
                <div className="miga">Miga de pan.../Miga de pan.../Miga de pan.../{busqueda}</div>
            </div>
            <div className="contenedor contenedorresultados" >

                {items && items.map(x => {
                    return <div className="resultado">
                        <div className="imagen" ><img width="90px" height="90" src={x.picture} /></div>
                        <div className="precio">{x.title}</div>
                        <div className="vendedor">{x.id}</div>
                    </div>;
                })}


            </div>
        </>


    )

}
export default Resultados;
