import React, { useState, createRef, useEffect }  from 'react';
import logo from './logo.svg';
import './App.css';
import { useHttp } from './hooks/useHttp'
import Cabecera from './componentes/Cabecera'
import logop from './images/logoP.png';



function App() {

  var [resultado] =  useHttp('http://localhost:3050/api/items/relog');
  // var items=[{id:1, title:'pi'}];
  var items =resultado.items;
  //  const [items, setItems] = useState([]);
  //  setItems(resultado.items);
  const poststring = JSON.stringify(resultado);


  var busqueda = React.createRef();

  return (
    <>
      <link rel='stylesheet' href='https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css'></link>
      <div className="body">

        <header role="banner" className="header">

          <div className="contenedor">

            <img className="imagen" src={logop} />

            <div className="divbuscar">
              <input type="text" className="inputbuscar" placeholder="Buscar" ref={busqueda} />

              <button className="botonbuscar" type="button" onClick={() => console.log(resultado)}>
                <i className="fa fa-search"></i>
              </button>
            </div>

          </div>

        </header>
        <div className="contenedor">
          <div className="miga">Miga de pan.../Miga de pan.../Miga de pan.../</div>
        </div>

        <div className="contenedor contenedorresultados" >
        {console.log(resultado.items)}

        {items && items.map(x => {
          return                 <div className="resultado">
          <div className="imagen" ><img width="150px" src="https://http2.mlstatic.com/reloj-para-ninos-super-heroes-D_NQ_NP_937156-MCO40024400605_122019-V.webp" /></div>
          <div className="precio">{x.title}</div>
          <div className="vendedor">{x.id}</div>
        </div>;
        })}


        </div>



        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />

          <p>
            Edit <code>src/App.js</code> and save to reload.
        </p>
          <p>
            {poststring}
          </p>

        </header>
      </div>
    </>
  );
}

export default App;
